import AllList from "./Components/AllList";
import MyList from "./Components/MyList";
import {useState} from 'react';
import './App.css';



function App() {
  const [myList , setmyList]= useState([]);

  function updateList(school){
    if(myList.indexOf(school) === -1)
      setmyList([...myList,school]);
  }
  function myClick(){
    return(
      <div><MyList mL={myList}/></div>
    )
  }

  const MyListClick = (e) =>{
    e.preventDefault();
    myClick();
  }

  function AllClick(){
    return(
      <div><AllList update={updateList}/></div>
    )
  }

  const AllListClick = (e) =>{
    e.preventDefault();
    myClick();
  }

  return (
    <div className="APP">
      <div className="navbar">
            <button onClick={MyListClick}>My List</button>
            <button onClick={() => <AllList update={updateList}/>}>University List </button>
        </div>
    </div>
  )
}

export default App;
