import React, { useState } from 'react';
import "./MyList.css";


export default function MyList(props) {
    const [names, setnames] = useState("");
    const [country, setcountry] = useState("");
    const [filter, setFilter] = useState(false);
    const [filteredList, setFilteredList] = useState([]);

    function filterMyList(name, country) {
        console.log(props.mL);
        var data = [];
        console.log(name);
        console.log(country);
        for (var j=0;j<props.mL.length;j++) {
            console.log(props.mL[j].name);
            console.log(props.mL[j].country);
            if (props.mL[j].name.toLowerCase() === name.toLowerCase() || props.mL[j].country.toLowerCase() === country.toLowerCase())
                data.push(props.mL[j]);
        }
        setFilteredList(data);
    }

    const onSubmit = (e) => {
        e.preventDefault();
        if (names === "" && country === "") {
            setFilter(false);
            return;
        }
        filterMyList(names, country);
        setFilter(true);
    }



    return (
        <div className="main">
            <div className="my">
                <h1>📜 My List 📜</h1>
                <div className="myLists">
                    {!filter && <div>{props.mL.map((m, i) => {

                        return (
                            <div className="main-box" key={i}>
                                <div className="School-names">
                                    <div className="myList-name" style={{ fontSize: "30px", color: "rgb(37, 15, 161)" }}><strong>{m["name"]}</strong></div>
                                    <div><strong>{m["country"]}</strong></div>
                                    <div>Domain: {m["domains"]}</div>
                                    <div>Website: {m["web_pages"]}</div>
                                </div>
                                <div className="up-down">
                                    <button className="mybutton" onClick={() => props.goUps(i)}>🔼</button>
                                    <button className="mybutton" onClick={() => props.goDowns(i)}>🔽</button>
                                </div>
                            </div>
                        )
                    })}
                    </div>}
                    {filter && <div>
                        {filteredList.map((m, i) => {

                            return (
                                <div className="main-box" key={i}>
                                    <div className="School-names">
                                        <div className="myList-name" style={{ fontSize: "30px", color: "rgb(37, 15, 161)" }}><strong>{m["name"]}</strong></div>
                                        <div><strong>{m["country"]}</strong></div>
                                        <div>Domain: {m["domains"]}</div>
                                        <div>Website: {m["web_pages"]}</div>
                                    </div>
                                </div>
                            )
                        })}
                    </div>
                    }
                </div>
            </div>
            <form className="App-search" onSubmit={onSubmit}>
                <h2>Filter Your Search</h2>
                <input className="input1" type="text" placeholder="Enter the name" value={names} onChange={(e) => setnames(e.target.value)} />

                <input className="input2" type="text" placeholder="Enter the country" value={country} onChange={(e) => setcountry(e.target.value)} />

                <input className="App-submit" type="submit" value="Search" />
            </form>
        </div>
    )
}
