import './App.css';
import Axios from "axios";
import { useState } from 'react';
function App() {
  const [query,setquery] = useState("");
  const [schools, setschool]= useState([]);

  var url = `http://universities.hipolabs.com/search?name=${query}`;
  async function getnames()
  {
    var result = await Axios.get(url); //api will take time to download so await is used.
    setschool(result.data.hits);
    console.log(result.data);
  }
  const onSubmit = (e) =>{
    e.preventDefault();
    getnames();
  }
  return (
    <div className="App">
      <h1>ğŸ“šUniversities around the World ğŸ“š</h1>
