        <input className="input1" type="text" placeholder="Enter the name" value={query} onChange={(e) => setquery(e.target.value)} />
        <input className="App-submit" type="submit" value="Search"/>
      </form>
      <div>
           <p>{schools}</p>;
      </div>
    </div>
  );
}

export default App;
