import React, { useEffect } from 'react'
import Axios from "axios";
import "./AllList.css"
import { useState } from 'react';

export default function AllList(props) {
  // const [names, setnames] = useState("");
  // const [country, setcountry] = useState("");
  // const [schools, setschool] = useState([]);
  const [value, setvalue] = useState("Add to My List");

  // const url = `http://universities.hipolabs.com/search?name=${names}&country=${country}`;


  // async function getnames() {
  //   console.log("getnames called");
  //   var result = await Axios.get(url);
  //   setschool(result.data);
  //   console.log(schools);
  // }



  const onSubmit = (e) => {
    e.preventDefault();
    props.getNames();
  }

  function clicked() {
    setvalue("Added");
  }

  // useEffect(() => {
  //   getnames();
  // }, []);

  return (
    <div className="App">
      <div className="App-context">
        <h1>📚 <span style={{ textDecoration: "underline" }}>Universities around the World</span> 📚</h1>

        <div loading="lazy">
          {props.schools.map((school, k) => {
            const addBtnTxt = props.myList.indexOf(school)!==-1?"Added":"Add to My List";
            return (
              <div key={k}>
                <div className="schools">
                  <div className="school-name" style={{ fontSize: "30px", color: "rgb(37, 15, 161)" }}><strong>{school["name"]}</strong></div>
                  <div><strong>{school["country"]}</strong></div>
                  <div>Domain: {school["domains"]} </div>
                  <div>Website: <a href={[school["web_pages"]]} >{[...school["web_pages"]]}</a></div>
                  <div><input className="Add-school" type="button" onClick={() => { clicked(); props.update(school) }} value={addBtnTxt} /></div>
                </div>
              </div>
            )
          }
          )}
        </div>
      </div>

      <form className="App-search" onSubmit={onSubmit}>
        <h2>Filter Your Search</h2>
        <input className="input1" type="text" autoComplete="off" placeholder="Enter the name" value={props.names} onChange={(e) => props.changeName(e.target.value)} />

        <input className="input2" type="text" autoComplete="off" placeholder="Enter the country" value={props.country} onChange={(e) => props.changecountry(e.target.value)} />

        <input className="App-submit" type="submit" value="Search" />
      </form>

    </div>
  );
}
