import AllList from "./Components/AllList";
import MyList from "./Components/MyList";
import {useState} from 'react';
import './App.css';



function App() {
  const [myList , setmyList]= useState([]);
  const [design, setDesign]=useState(<AllList update={updateList}/>);
  function updateList(school){
    if(myList.indexOf(school) === -1)
      setmyList([...myList,school]);
  }

  function button1()
  {

    setDesign( <MyList mL={myList}/>);
    console.log(myList);
  } 

  function button2()
  {
    e.preventDefault();

    setDesign (<AllList update={updateList}/>);
    console.log(updateList);
  } 


  return (
    < div className="APP">
      <div className="navbar">
            <button onClick={button1}>My List</button>
            <button onClick={button2}>University List </button>
        </div>
        {design}
    </div>
  )
}

export default App;
