import AllList from "./Components/AllList";
import MyList from "./Components/MyList";
import {useState, useEffect} from 'react';
import Axios from "axios";
import './App.css';




function App() {
  const [myList , setmyList]= useState([]);
  const [design, setDesign]=useState(true);
  const [schools, setschool] = useState([]);
  const [names, setnames] = useState("");
  const [country, setcountry] = useState("");

  const url = `http://universities.hipolabs.com/search?name=${names}&country=${country}`;
  async function getnames() {
    console.log("getnames called");
    var result = await Axios.get(url);
    setschool(result.data);
    console.log(schools);
  }
  useEffect(() => {
    getnames();
  }, []);

  function changeName(name){
    setnames(name);
  }
  function changecountry(country){
    setcountry(country);
  }

  function updateList(school){
    if(myList.indexOf(school) === -1){
      setmyList([...myList,school]);
    }
  }

  function button1()
  {

    setDesign( false);
  } 

  function button2()
  {

    setDesign (true);
  } 

  function goUp(i)
  {
    let datas=[...myList];

    if(i===0)
      return;

    var tmp=datas[i-1];
    datas[i-1]=datas[i];
    datas[i]=tmp;

    setmyList(datas);
  }

  function goDown(i)
  {
    let datas=[...myList];
    if(i===datas.length-1)
      return;

    var tmp=datas[i];
    datas[i]=datas[i+1];
    datas[i+1]=tmp;
    setmyList(datas);
  }


  return (
    < div className="APP">
      <div className="navbar">
            <button className="nav-button" onClick={button1}>My List</button>
            <button className="nav-button" onClick={button2}>University List </button>
        </div>
        {design && <AllList update={updateList} myList={myList} changeName={changeName} changecountry={changecountry} name={names} country={country} schools={schools} getNames={getnames} />}
        {!design && <MyList mL={myList} goUps={goUp} goDowns={goDown} />}

    </div>
  )
}

export default App;
