import AllList from "./Components/AllList";
import MyList from "./Components/MyList";
import {useState} from 'react';
import './App.css';



function App() {
  const [myList , setmyList]= useState([]);
  const [design, setDesign]=useState(true);
  function updateList(school){
    if(myList.indexOf(school) === -1)
      setmyList([...myList,school]);
  }

  function button1()
  {

    setDesign( false);
    console.log(myList);
  } 

  function button2()
  {

    setDesign (true);
    console.log(updateList);
  } 

  function goUp(i)
  {
      setmyList(myList => {
        let data=[...myList];
        if(i!==0)
        {
          let t=data[i];
        data[i]=data[i-1];
        data[i-1]=t;
        }
        return data;
      })
  }

  function goDown(i)
  {
    setmyList(myList => {
      let data=[...myList];
      if(i!==(myList.length))
      return data;
    })
  }

  return (
    < div className="APP">
      <div className="navbar">
            <button className="nav-button" onClick={button1}>My List</button>
            <button className="nav-button" onClick={button2}>University List </button>
        </div>
        {design && <AllList update={updateList}/>}
        {!design && <MyList mL={myList} goUps={goUp} goDowns={goDown} />}
    </div>
  )
}

export default App;
