import React, { useState } from 'react'
import "./AllList.css";
import "./MyList.css";


export default function MyList(props) {
   const [names,setnames]= useState("");
   const [country, setcountry]= useState("");
    var arr=[];
   const onSubmit=(e) => {
        e.preventDefault();
        for(let m in props.mL)
        {
            if((m["name"].indexOf(names) != -1) && (m["country"].indexOf(country) != -1))
                arr.push(m);

        }
        props.mL=arr;
   }

    return (
        <div className="my">
            <h1>ðŸ“œ My List ðŸ“œ</h1>
            <div className="myLists">
                <div>{props.mL.map((m) => {
                    return (
                        <div>
                            <div>
                                <div className="myList-name" style={{fontSize:"30px", color: "rgb(37, 15, 161)"}}><strong>{m["name"]}</strong></div>
                                <div><strong>{m["country"]}</strong></div>
                                <div>Domain: {m["domains"]}</div>
                                <div>Website: {m["web_pages"]}</div>
                            </div>
                            <div>
                                <form className="App-search" onSubmit={onSubmit}>
                                    <h2>Filter Your Search</h2>
                                    <input className="input1" type="text" placeholder="Enter the name" value={names} onChange={(e) => setnames(e.target.value)} />

                                    <input className="input2" type="text" placeholder="Enter the country" value={country} onChange={(e) => setcountry(e.target.value)} />

                                    <input className="App-submit" type="submit" value="Search"/>
                                </form>
                            </div>
                        </div>
                    )})}
                    </div>
            </div>
        </div>
    )
}
