import AllList from "./Components/AllList";
import MyList from "./Components/MyList";
import {useState} from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";



function App() {
  const [myList , setmyList]= useState([]);
  const [design, setDesign]=useState(true);
  function updateList(school){
    if(myList.indexOf(school) === -1)
      setmyList([...myList,school]);
  }

  function button1()
  {

    setDesign( false);
  } 

  function button2()
  {

    setDesign (true);
  } 

  function goUp(i)
  {
    let datas=[...myList];
      setmyList(datas => {

        if(i!==0)
        {
          let t=datas[i];
          datas[i]=datas[i-1];
          datas[i-1]=t;
        }
        return datas;
      })
  }

  function goDown(i)
  {
    let datas=[...myList];
    setmyList(datas => {

      if(i!==(datas.length -1))
      {
        console.log(datas[i]);
        let t=datas[i];
        datas[i]=datas[i+1];
        datas[i+1]=t;
      }
      return datas;
    })
  }

  return (
    < div className="APP">


        <Router>
          <div>

          <Switch>
            <Route path="/">{design && <AllList update={updateList}/>}</Route>
            <Route path="/mylist">{!design && <MyList mL={myList} goUps={goUp} goDowns={goDown} />}</Route>
          </Switch>
          </div>
        </Router>
    </div>
  )
}

export default App;
