import AllList from "./Components/AllList";
import MyList from "./Components/MyList";
import {useState} from 'react';
import './App.css';



function App() {
  const [myList , setmyList]= useState([]);
  const [design, setDesign]=useState(true);
  function updateList(school){
    if(myList.indexOf(school) === -1)
      setmyList([...myList,school]);
  }

  function button1()
  {

    setDesign( false);
    console.log(myList);
  } 

  function button2()
  {

    setDesign (true);
    console.log(updateList);
  } 


  return (
    < div className="APP">
      <div className="navbar">
            <button style={{margin:"0px 20px 0px 0px"}} onClick={button1}>My List</button>
            <button onClick={button2}>University List </button>
        </div>
        {design && <AllList update={updateList}/>}
        {!design && <MyList mL={myList}/>}
    </div>
  )
}

export default App;
