import React, { Component } from 'react';
import Loader from '../../shared/loader/loader'
import UniversityList from '../../Components/universityList'

class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {country : 'All'};
        this.onChangeCountry = this.onChangeCountry.bind(this);
      }

      onChangeCountry(country) {
          this.setState({country});
      }

    componentDidMount () {
       const { getAllUniversity, getBucket }  = this.props 
       getAllUniversity();
       getBucket()
    }

    render() {
        const { fetchingUniversities, universities, universitiesByCountry } = this.props; 
        const { country } = this.state;
        const countries = Object.keys(universitiesByCountry || {})
        const universityArray = (universitiesByCountry && universitiesByCountry[country]) || universities;
        return <div>{ fetchingUniversities ? <Loader/> : <UniversityList universityArray={universityArray} title = "All Universities"/>}
        </div>
    }
}
export default Home;
