import React from 'react'
import Axios from "axios";
import "./AllList.css"
import { useState } from 'react';

export default function AllList(props) {
  const [names,setnames] = useState("");
  const [country, setcountry] = useState("");
  const [schools, setschool]= useState([]);
  const [value,setvalue] = useState("Add to My List");

  var url = `http://universities.hipolabs.com/search?name=${names}&country=${country}`;


  async function getnames()
  {
    var result = await Axios.get(url); 
    setschool(result.data);
    console.log(result.data);
  }


  const onSubmit = (e) =>{
    e.preventDefault();
    getnames();
  }

  function clicked()
  {
    setvalue("Added");

  }

  return (
    <div className="App">
      <div className="App-context">
        <h1>📚 <span style={{textDecoration:"underline"}}>Universities around the World</span> 📚</h1>

        <div>
          {schools.map((school,k)=> 
          {
          return (
          <div>
            <div className="schools">
              <div className="school-name" style={{fontSize:"30px", color: "rgb(37, 15, 161)"}}><strong>{school["name"]}</strong></div>
              <div><strong>{school["country"]}</strong></div>
              <div>Domain: {school["domains"]} </div>
              <div>Website: <a href={[school["web_pages"]]} >{[...school["web_pages"]]}</a></div>
              <div><input className="Add-school" type="button" onClick={() => props.update(school)} value={value}/></div>
            </div>
          </div>   
          )
          }
          )}
        </div>
       </div>

      <form className="App-search" onSubmit={onSubmit}>
        <h2>Filter Your Search</h2>
        <input className="input1" type="text" placeholder="Enter the name" value={names} onChange={(e) => setnames(e.target.value)} />

        <input className="input2" type="text" placeholder="Enter the country" value={country} onChange={(e) => setcountry(e.target.value)} />

        <input className="App-submit" type="submit" value="Search"/>
      </form>
      <div>{getnames}</div>

    </div>
  );
}
